import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { RootState } from './../store';
import axios from 'axios';

interface LoginResponse {
  _id: string;
  name: string;
  email: string;
  token: string;
}

interface LoginState {
  user: LoginResponse | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  error: null;
}

interface LoginData {
  name?: string;
  email: string;
  password: string;
}

export const loginUser = createAsyncThunk<LoginResponse, LoginData>(
  'auth/login',
  async ({ email, password }, { rejectWithValue }) => {
    /*alert(
      'process.env.REACT_APP_API_SERVER_URL}/users/login: ' +
        `${process.env.REACT_APP_API_SERVER_URL}` +
        '/users/login',
    );*/
    try {
      const response = await axios.post<LoginResponse>(
        `${process.env.REACT_APP_API_SERVER_URL}/users/login`,
        //`/users/login`,
        {
          email,
          password,
        },
      );
      alert('Login successful');
      return response.data;
    } catch (error) {
      console.log(error);
      alert(
        'process.env.REACT_API_SERVER_URL}/users/login: ' +
          `${process.env.REACT_APP_API_SERVER_URL}/users/login`,
      );
      alert('Hubo un error: ');

      if (axios.isAxiosError(error)) {
        return rejectWithValue(error.response?.data);
      }
      throw error;
    }
  },
);

export const registerUser = createAsyncThunk<LoginResponse, LoginData>(
  'auth/login',
  async ({ name, email, password }, { rejectWithValue }) => {
    try {
      const response = await axios.post<LoginResponse>(
        `${process.env.REACT_API_SERVER_URL}/users/register`,
        //`/users/register`,
        {
          name,
          email,
          password,
        },
      );
      return response.data;
    } catch (error) {
      console.log(error);
      if (axios.isAxiosError(error)) {
        return rejectWithValue(error.response?.data);
      }
      throw error;
    }
  },
);

const initialState: LoginState = {
  user: null,
  isAuthenticated: false,
  isLoading: false,
  error: null,
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    logout: (state) => {
      state.user = null;
      state.isAuthenticated = false;
      localStorage.removeItem('token');
    },
  },
  extraReducers: (builder) => {
    builder.addCase(loginUser.pending, (state) => {
      state.isLoading = true;
      state.error = null;
    });
    builder.addCase(loginUser.fulfilled, (state, action) => {
      state.isLoading = false;
      state.isAuthenticated = true;
      state.user = action.payload;
      localStorage.setItem('token', action.payload.token);
    });
    builder.addCase(loginUser.rejected, (state, action) => {
      state.isLoading = false;
      state.isAuthenticated = false;
    });
  },
});

export const { logout } = authSlice.actions;

export const selectUser = (state: RootState) => state.auth.user;
export const selectUserId = (state: RootState) => state.auth.user?._id;
export const selectIsAuthenticated = (state: RootState) => state.auth.isAuthenticated;

export const selectIsLoading = (state: RootState) => state.auth.isLoading;
export const selectError = (state: RootState) => state.auth.error;
export default authSlice.reducer;
